      clip: false
    }),

    // B. LEFT ANNOTATION: P(E|H)
    Plot.ruleX([1], {
      x: -0.03,          
      y1: 0, y2: like_alex, 
      stroke: "#6ecae1", strokeWidth: 2,
      clip: false
    }),
    Plot.text([1], {
      x: -0.03,          
      y: like_alex / 2, 
      dx: -15, // Push slightly left away from the line
      rotate: -90, // &lt;--- Rotates text vertically
      text: () =&gt; `P(D|A) = ${like_alex.toFixed(2)}`, // Removed \n for cleaner vertical look
      fill: "#6ecae1", 
      textAnchor: "middle", // Centers text on the bar height
      fontWeight: "bold", fontSize: 16, clip: false
    }),

    // C. RIGHT ANNOTATION: P(E|¬H)
    Plot.ruleX([1], {
