  // 3. The "Evidence" Box (The bottom colored part)
  {
    label: "Dương giả",
    x1: prev, 
    x2: 1, 
    y1: 0, 
    y2: 1 - spec, 
    color: "#2f7e9b" // Teal (Competing Evidence)
  },

  // 4. The "Void" Box (The top dark part)
  {
    label: "Âm thật",
    x1: prev, 
    x2: 1, 
    y1: 1 - spec, 
    y2: 1, 
    color: "#0f0f0f" // Black
